import React, { useState } from "react";
import { useHistory } from "react-router-dom";

const Cards = () => {
    let history = useHistory();
  const [newarray, setnewarray] = useState([
    {
      id: 1,
      name: "tokio",
      img: "https://m.media-amazon.com/images/I/515zK++84iS._AC_SY1000_.jpg",
    },
    {
      id: 2,
      name: "newYork",
      img: "https://cdn.cnn.com/cnnnext/dam/assets/210409114908-nyc-april-2021-tease-super-tease.jpg",
    },
  ]);

  console.log(useHistory());

  const goInside = (id) => {
    console.log(id);
    history.push("/card/:$id");
  };

  return (
    <div>
      {newarray.map((item) => {
        return (
          <div
            onClick={() => {
              goInside(item.id);
            }}
          >
            {item.id}
            {item.name}
            <img src={item.img} alt="city" />
          </div>
        );
      })}
    </div>
  );
};

export default Cards;
