import React, { useState } from "react";
import { useParams } from "react-router-dom";
import './style.css';

const Card = () => {
  const id = useParams().id;
  const [newarray, setnewarray] = useState([
    {
      id: 1,
      name: "tokio",
      img: "https://m.media-amazon.com/images/I/515zK++84iS._AC_SY1000_.jpg",
    },
    {
      id: 2,
      name: "newYork",
      img: "https://cdn.cnn.com/cnnnext/dam/assets/210409114908-nyc-april-2021-tease-super-tease.jpg",
    },
  ]);

  const item = newarray.find((element) => element.id == id);
  {
    console.log(item.name);
  }

  return (
    <div className="">
      {item.name}
      <img src={item.img} alt="city" />
    </div>
  );
};

export default Card;
